startup:
if [ "{{ALLOW_UPDATE}}" = "1" ] || [ ! -f /home/container/game.zip ];
then /home/container/hytale-downloader -patchline {{HYTALE_PATCHLINE}} -download-path /home/container/game.zip;
unzip  -o game.zip;
fi;

/home/container/hytale-downloader -print-version;


echo "java -Xms128M -Xmx{{SERVER_MEMORY}}M $( [ "{{USE_AOT_CACHE}}" = "1" ] && echo "-XX:AOTCache=/home/container/Server/HytaleServer.aot" )  -jar /home/container/Server/{{JAR_FILE}}   --assets /home/container/{{ASSET_FILE}}   --bind 0.0.0.0:{{SERVER_PORT}}    --backup-dir {{BACKUP_DIR}}   --backup-frequency {{BACKUP_FREQ}}    $( [ "{{BACKUPS}}" = "1" ] && echo "--backups" )   $( [ "{{HYTALE_ALLOW_OP}}" = "1" ] && echo "--allow-op" )    {{ARGS}}";

echo "IF YOU NEED TO UPDATE THE GAME TICK ALLOW UPDATE ON THE STARTUP TAB";

java -Xms128M -Xmx{{SERVER_MEMORY}}M $( [ "{{USE_AOT_CACHE}}" = "1" ] && echo "-XX:AOTCache=/home/container/Server/HytaleServer.aot" )  -jar /home/container/Server/{{JAR_FILE}}   --assets /home/container/{{ASSET_FILE}}   --bind 0.0.0.0:{{SERVER_PORT}}    --backup-dir {{BACKUP_DIR}}   --backup-frequency {{BACKUP_FREQ}}    $( [ "{{BACKUPS}}" = "1" ] && echo "--backups" )   $( [ "{{HYTALE_ALLOW_OP}}" = "1" ] && echo "--allow-op" )    {{ARGS}}
